# Create a new file
touch <File>

# Create a new directory
mkdir <Dir>

# Print working directory
pwd 

# Remove a file
rm <File>
# Remove a directory
rm [-r|f] <Dir>

# Print
echo $var
# Add or Insert
echo "<string>" > <file> # Add
echo "<string>" >> <file> # Insert

# Copy
cp <file> <dir>
# Copy recursively
cp -rf <dir>/* <dir>

# Rename or move a file or a directory
mv <file_name> <new_file_name>

# Placeholder
# All e.g., cd 1*e (1-Sample)
* 



